import React from "react";
import deleteButton from "./deleteButton.svg";
import { useState } from "react";
import "./styles/taskList.css";
function TaskList({ selectedCategory, category }) {
  const [styledIndices, setStyledIndices] = useState([]);

  const handleStyles = (index) => {
    if (styledIndices.includes(index)) {
      // Remove the index from the array if it's already present
      setStyledIndices((prev) => prev.filter((i) => i !== index));
    } else {
      // Add the index to the array
      setStyledIndices((prev) => [...prev, index]);
    }
  };
  return (
    <div className="list-container">
      {selectedCategory.map((task, index) => (
        <div key={index} className="list-item">
          <div className="checkbox" onClick={handleStyles}></div>
          <p style={isStyled ? { textDecoration: "line-through" } : {}}>
            {task}
          </p>{" "}
          <div className="category">{category}</div>
          <div className="delete">
            <img src={deleteButton} alt="delete" />
          </div>
        </div>
      ))}
    </div>
  );
}

export default TaskList;
