import "./styles/todo.css";
import Categories from "./Categories";
import Tasks from "./Tasks";
import { useState } from "react";

function ToDo() {
  const categoriesList = ["All", "Groceries", "College", "Payments"];
  const [category, setCategory] = useState("UnCategorized");

  const highlight = (cat) => {
    return cat === category ? { color: "#EA5959" } : { color: "#525252" };
  };

  const [tasks, setTasks] = useState({
    unCategorized: ["Get new helmet", "Replace laptop's screen"],
    groceries: ["Purchase Milk and corn flakes"],
    college: ["Complete Assignments"],
    payments: ["Pay mortgage"],
  });

  let selectedTasks;
  if (category === "All") {
    selectedTasks = [].concat(...Object.values(tasks));
  } else {
    selectedTasks = tasks[category.toLowerCase()] || tasks.unCategorized;
  }

  const handleInput = (e) => {
    return e.target.value;
  };

  const handleUncategorized = () => {
    return category === "All" ? "Uncategorized" : category;
  };

  return (
    <div className="todo-app">
      <Categories
        categoryList={categoriesList}
        highlight={highlight}
        selectCategory={setCategory}
      />
      <Tasks
        handleInput={handleInput}
        category={category}
        handleUncategorized={handleUncategorized}
        selectedCategory={selectedTasks}
      />
    </div>
  );
}

export default ToDo;
