import "./styles/tasks.css";
import TaskList from "./TaskList";
function Tasks({
  handleInput,
  category,
  handleUncategorized,
  selectedCategory,
  setCategoryState,
  value,
}) {
  return (
    <div className="tasks">
      <h1>{category} Tasks</h1>
      <input
        value={value}
        type="text"
        onChange={handleInput}
        placeholder={`Add new task inside '${category}' category`}
      />
      <TaskList selectedCategory={selectedCategory} category={category} />
    </div>
  );
}

export default Tasks;
